    private void jButton17ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton17ActionPerformed
     try {
        double valor1Double = Double.parseDouble(valor1.getText());
        double valor1Radianos = Math.toRadians(valor1Double); // Convertendo graus para radianos
        double seno = Math.sin(valor1Radianos);
        resultado.setText(String.valueOf(seno));
    } catch (NumberFormatException e) {
        resultado.setText("Entrada inválida");
    }    // TODO add your handling code here:
    }//GEN-LAST:event_jButton17ActionPerformed

    private void jButton18ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton18ActionPerformed
      try {
        double valor1Double = Double.parseDouble(valor1.getText());
        double valor1Radianos = Math.toRadians(valor1Double); // Convertendo graus para radianos
        double cosseno = Math.cos(valor1Radianos);
        resultado.setText(String.valueOf(cosseno));
    } catch (NumberFormatException e) {
        resultado.setText("Entrada inválida");
    }     // TODO add your handling code here:
    }//GEN-LAST:event_jButton18ActionPerformed

    private void jButton21ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton21ActionPerformed
       try {
        double valor1Double = Double.parseDouble(valor1.getText());
        double valor1Radianos = Math.toRadians(valor1Double); // Convertendo graus para radianos
        double tangente = Math.tan(valor1Radianos);
        resultado.setText(String.valueOf(tangente));
    } catch (NumberFormatException e) {
        resultado.setText("Entrada inválida");
    }    // TODO add your handling code here:
    }//GEN-LAST:event_jButton21ActionPerformed

