    private void jButton22ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton22ActionPerformed
        double valor1Double = Double.parseDouble(valor1.getText());
    double exponencial = Math.exp(valor1Double);
    resultado.setText(String.valueOf(exponencial));        // TODO add your handling code here:
    }//GEN-LAST:event_jButton22ActionPerformed

    private void jButton24ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton24ActionPerformed
      double valor1Double = Double.parseDouble(valor1.getText());
    double valor2Double = Double.parseDouble(valor2.getText());
    double multiplicacao = valor1Double * valor2Double;
    resultado.setText(String.valueOf(multiplicacao));     // TODO add your handling code here:
    }//GEN-LAST:event_jButton24ActionPerformed

