    private void jButton19ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton19ActionPerformed
        double valor1Double = Double.parseDouble(valor1.getText());
    double valor2Double = Double.parseDouble(valor2.getText());
    double porcentagem = (valor1Double * valor2Double) / 100;
    resultado.setText(String.valueOf(porcentagem));  // TODO add your handling code here:
    }//GEN-LAST:event_jButton19ActionPerformed

