    private void jButton26ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton26ActionPerformed
     double valor1Double = Double.parseDouble(valor1.getText());
    double fatorial = 1;
    for (int i = 1; i <= valor1Double; i++) {
        fatorial *= i;
    }
    resultado.setText(String.valueOf(fatorial));   // TODO add your handling code here:
    }//GEN-LAST:event_jButton26ActionPerformed

